import React, { useState, useEffect } from 'react'
import { getMood } from './api/getMood'
import { MoodContent } from './components/MoodContent'
import { PieChart, Pie, Cell } from "recharts";

import './Top.css'
// import { CategoryList } from '../components/CategoryList'


// added
const data = [
  { name: 'Group A', value: ((MoodContent.mood==='rad').length) },
  { name: 'Group B', value: ((MoodContent.mood==='good').length) },
  { name: 'Group C', value: 300 },
  { name: 'Group D', value: 200 },
];

const COLORS = ['#FFCC33', '#98FB98', '#DDA0DD', '#D3D3D3'];

const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent, index }) => {
  const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};





export const Top = () => {
  const initialState = {
    id: '',
    date: '',
    mood: '',
  }

  const[mood, setMood] = useState(initialState);
  const[loading, setLoading] = useState(true);


  useEffect(() => {
    getMood()
    .then(d => {
      setMood(d)
      setLoading(false)
    })
    .catch(err => console.log(err));
  },[])

  // added
  let radCount, goodCount, mehCount, badCount = 0;





  return(
    <div>
      {
        loading ?
        <h1>loading...</h1>
        :
        <div>

          <div className='Top'>
          {mood.map( d => <MoodContent {...d}  /> )}
          </div>

          <div className='pie-chart-parent'>
          { mood.mood === 'rad' ? radCount += 1
          : mood.mood === 'good' ? goodCount += 1
          : mood.mood === 'meh' ? mehCount += 1
          : badCount += 1
          }
            <PieChart className='pie-chart-child' width={400} height={400}>
              <Pie
                data={data}
                cx={200}
                cy={200}
                labelLine={false}
                label={renderCustomizedLabel}
                outerRadius={150}
                fill="#8884d8"
                dataKey="value"
              >
                {data.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
            </PieChart>
          </div>

        </div>

      }
    </div>
  )

}
